function focus_harga(elemen) {var x = elemen.value.replaceAll(".",""); x = x.replaceAll(",","."); document.getElementById(elemen.id).value = x; } function blur_harga(elemen) {var x = elemen.value.replaceAll(".", ","); x = x.replace(/\B(?=(\d{3})+(?!\d))/g, "."); document.getElementById(elemen.id).value = x; } function to_normal(val) {var x = val.replaceAll(".",""); x = x.replaceAll(",","."); return x; } function to_decimal(val) {var x = val.toString().replaceAll(".", ","); var q = x.replace(/\B(?=(\d{3})+(?!\d))/g, "."); return q; }